import { ref } from "vue";
import $ from "jquery";
import { apiUrl } from "../../config";
import router from "@/router";
import usePeerStore from "@/store/peer";
// import initializeWebsocket from "@/plugins/initialize-websocket";
// import initializePeer from "@/plugins/initialize-peer";

export default {
  __name: 'LoginView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const username = ref('');
    const password = ref('');
    const peerStore = usePeerStore();
    const message = ref('');
    const login = () => {
      $.ajax({
        url: `${apiUrl}/api/user/account/token/`,
        type: "post",
        data: {
          username: username.value,
          password: password.value
        },
        success(resp) {
          if (resp.code === 200) {
            peerStore.token = resp.data.token;
            getInfo();
            router.push("/setting-view");
          } else {
            message.value = resp.message;
          }
        }
      });
    };
    function getInfo() {
      $.ajax({
        url: `${apiUrl}/api/user/account/info/`,
        type: "get",
        headers: {
          Authorization: "Bearer " + peerStore.token
        },
        success(resp) {
          if (resp.code === 200) {
            peerStore.username = resp.data.username;
            peerStore.is_login = true;
            peerStore.phone = resp.data.phone;
            peerStore.role = resp.data.role;
            router.push("/setting-view");
            // initializePeer(username.value);
            // initializeWebsocket(username.value);
          } else {
            console.log(resp.message);
          }
        },
        error(resp) {
          console.log(resp);
        }
      });
    }
    const jwt_token = localStorage.getItem("jwt_token");
    if (jwt_token) {
      peerStore.token = jwt_token;
      try {
        getInfo({
          success() {
            router.push("/setting-view");
            peerStore.pulling_info = false;
          },
          error() {
            peerStore.pulling_info = false;
          }
        });
        peerStore.pulling_info = false;
      } catch (e) {
        console.log(e);
        peerStore.pulling_info = false;
      }
    } else {
      peerStore.pulling_info = false;
    }
    const __returned__ = {
      username,
      password,
      peerStore,
      message,
      login,
      getInfo,
      jwt_token,
      ref,
      get $() {
        return $;
      },
      get apiUrl() {
        return apiUrl;
      },
      get router() {
        return router;
      },
      get usePeerStore() {
        return usePeerStore;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
